<?php require_once "support/common/connectdb.php"; ?><?phpfunction getProductionById($id){	global $con;	try{		$sql = "SELECT * FROM eb_production WHERE id='".$id."'";		$result = mysql_query($sql);		$production = array();		while($row = mysql_fetch_array($result)){//遍历后台用户表数据			$production = $row;			break;		}	}catch(Exception $e){		mysql_close($con);		return $production;	}	mysql_close($con);	return $production;}?>