<?phprequire_once("../common/checkLogin.php");require_once("../common/connectdb.php");try{  $id = $_GET["id"];    if(!isset($id) || trim($id) == ""){    echo "要修改的用户id不能为空 ！";	mysql_close($con);	return;  }    $sql = "select * from su_operator where id='".$id."'";  $result = mysql_query($sql);    $id = "";  $name = "";  $username = "";  $password = "";  while($row = mysql_fetch_array($result)){//遍历后台用户表数据	$id = $row["id"];    $name = $row["name"];	$username = $row["username"];	$password = $row["password"];  }    if($username == ""){    echo "要修改的用户不存在 ！";	mysql_close($con);	return;  }  }catch(Exception $e){  mysql_close($con);}mysql_close($con);?>